import { GET_VIDEOGAMES, GET_VIDEOGAME_BY_ID, GET_VIDEOGAMES_BY_NAME, GET_GENRES, RESET, FILTER, FILTER_BY_GENRE, FILTER_BY_SOURCE } from '../reduxConstants';

const initialState = {
    videogame: {},
    videogames: [],
    genres: [],
    filter: []
};

export default function rootReducer(state = initialState, action) {

    if (action.type === GET_VIDEOGAMES) {
        return {
            ...state,
            videogames: action.payload
        }
    }

    if (action.type === GET_GENRES) {
        return {
            ...state,
            genres: action.payload
        }
    }

    if (action.type === GET_VIDEOGAME_BY_ID) {
        return {
            ...state,
            videogame: action.payload
        }
    }

    if (action.type === GET_VIDEOGAMES_BY_NAME) {
        return {
            ...state,
            videogames: action.payload
        }
    }

    if (action.type === FILTER) {
        return {
            ...state,
            filter: action.payload
        }
    }

    if (action.type === RESET) {
        return {
            ...state,
            filter: []
        }
    }

    if (action.type === FILTER_BY_GENRE) {
        return {
            ...state,
            filter_source: action.payload
        }
    }

    if (action.type === FILTER_BY_SOURCE) {
        return {
            ...state,
            filter_genre: action.payload
        }
    }

    return state
}
