import { GET_VIDEOGAMES, GET_VIDEOGAME_BY_ID, GET_VIDEOGAMES_BY_NAME, GET_GENRES, GET_VIDEOGAMES_FILTER, FILTER_BY_GENRE, FILTER } from '../reduxConstants';

const initialState = {
    videogame: {},
    videogames: [],
    genres: [],
    filter: []
};

export default function rootReducer(state = initialState, action) {

    if (action.type === GET_VIDEOGAMES) {
        return {
            ...state,
            videogames: action.payload
        }
    }

    if (action.type === GET_GENRES) {
        return {
            ...state,
            genres: action.payload
        }
    }

    if (action.type === GET_VIDEOGAME_BY_ID) {
        return {
            ...state,
            videogame: action.payload
        }
    }

    if (action.type === GET_VIDEOGAMES_BY_NAME) {
        return {
            ...state,
            videogames: action.payload
        }
    }

    if (action.type === FILTER_BY_GENRE) {
        let videogamesGenre;
        
        if (action.payload === "All") {
            videogamesGenre = state.videogames
        } else {
            videogamesGenre = state.videogames.filter(g => g.genres.includes(action.payload))
        }

        return {
            ...state,
            filter: videogamesGenre
        }
    }

    if (action.type === FILTER) {
        return {
            ...state,
            filter: [...state.filter]
        }
    }

    return state
}
