import React from 'react';
import { createVideogame } from '../../redux/actions';
import { useDispatch } from 'react-redux';



const Create = () => {
    const dispatch = useDispatch()
    const [input, setInput] = React.useState({
        name: "",
        description: "",
        launchingdate: 0,
        rating: 0,
        platform: "",
        genres: []
    })
    const handleInputChange = function (e) {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        });
    }
    function handleSubmit(e) {
        e.preventDefault()
        dispatch(createVideogame(input))
    }

    return (

        <form onSubmit={e => handleSubmit(e)}>


            <label>Name: </label>
            <input onChange={handleInputChange} name="name"></input>
            <label>Description: </label>
            <input onChange={handleInputChange}  name="description"></input>
            <label>Launching Date: </label>
            <textarea onChange={handleInputChange} type="number" name="launchingdate"></textarea>
            <label>Rating: </label>
            <input onChange={handleInputChange} type="number" name="rating"></input>
            <label>Platform: </label>
            <input onChange={handleInputChange}  name="platform"></input>
            <label>Genres: </label>
            <select onChange={handleInputChange}  name="genres"></select>
            <button type="submit">Videogame</button>


        </form>

    );
};

export default Create;
