import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { filterVideogames, getGenres, getVideogames, resetVideogames } from "../../redux/actions";
import { getFilteredVideogames, order } from "../../controllers/order"

export default function Filters() {
    const dispatch = useDispatch();
    let videogames = useSelector(state => state.videogames);
    let filterState = useSelector(state => state.filter)

    useEffect(() => {
        dispatch(getGenres())
    }, []);

    let genres = useSelector(state => state.genres);

    function handleChange(e) {
        e.preventDefault()
        let filtered = getFilteredVideogames(videogames, e.target.value)
        dispatch(filterVideogames(filtered))
    }

    function handleOrderChange(e){
        e.preventDefault()
        videogames = filterState.length ? filterState : videogames
        if(e.target.value === "high"){
            dispatch(filterVideogames(order(videogames, "rating")))
        }
        if(e.target.value === "low"){
            dispatch(filterVideogames(order(videogames, "rating", "reverse")))
        }
        if(e.target.value === "az"){
            dispatch(filterVideogames(order(videogames, "name")))
        }
        if(e.target.value === "za"){
            dispatch(filterVideogames(order(videogames, "name", "reverse")))
        }
    }

    function handleReset(){
        dispatch(resetVideogames())
    }

    return (
        <div >
            <select onChange={(e) => handleOrderChange(e)}>
                <option>Sort</option>
                <option value="az">A-Z</option>
                <option value="za">Z-A</option>
                <option value="high">Best rating</option>
                <option value="low">Worst rating</option>
            </select>
            <select onChange={(e) => handleChange(e)}>
                <option >Filter by Source</option>
                <option value="all">All</option>
                <option value="api">API</option>
                <option value="created">Created</option>
            </select>
            <select onChange={(e) => handleChange(e)}>
                <option >Filter by Genres</option>
                <option value="all">All</option>
                {genres.map(g => <option value={g.name}>{g.name}</option>)}
            </select>
            <button onClick={handleReset}>Reset Filters</button>
        </div>
    )
}
