import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { filterVideogames, getGenres } from "../../redux/actions";
import { getFilteredVideogames } from "../../controllers/order"

export default function Filters() {
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getGenres())
    }, [dispatch]);
    let genres = useSelector(state => state.genres);
    let videogames = useSelector(state => state.videogames);

    let [filter, setFilter] = useState("");

    function handleGenreChange(e) {
        e.preventDefault()
        setFilter(e.target.value)
        let filtered = getFilteredVideogames(videogames, filter)
        dispatch(filterVideogames(filtered))
    }

    return (
        <div >
            <select >
                <option>Sort Alphabetically</option>
                <option value="az">A-Z</option>
                <option value="za">Z-A</option>
            </select>
            <select >
                <option >Sort Rating</option>
                <option value="all">All</option>
                <option value="best">Best rating</option>
                <option value="worst">Worst rating</option>
            </select>
            <select >
                <option >Filter by Source</option>
                <option value="all">All</option>
                <option value="api">API</option>
                <option value="created">Created</option>
            </select>
            <select onChange={(e) => handleGenreChange(e)}>
                <option >Filter by Genres</option>
                <option value="all">All</option>
                {genres.map(g => <option name={g.name}>{g.name}</option>)}
            </select>
            <button onClick={"xd"}>Reset Filters</button>
        </div>
    )
}
