import React from 'react';
import { createProduct } from '../../redux/actions';
import { useDispatch } from 'react-redux';
// Fijense en los test que SI O SI tiene que ser un functional component, de otra forma NO VAN A PASAR LOS TEST
// Deben usar Hooks para que los test pasen.
// No realicen un destructuring de ellos, sino que utilicenlos de la siguiente forma 'React.useState' y 'React.useEffect' ,
// Si no lo hacen asi los test no van a correr.



const Create = () => {
   const dispatch = useDispatch()
   const [input, setInput] = React.useState({
      name: '',
      price: 0,
      description: '',
      stock: 0,
   })
   const handleInputChange = function (e) {
      setInput({
         ...input,
         [e.target.name]: e.target.value
      });
   }
   function handleSubmit(e) {
      e.preventDefault()
      dispatch(createProduct(input))
   }

   return (

      <form onSubmit={e => handleSubmit(e)}>


         <label>Name: </label>
         <input onChange={handleInputChange} name="name"></input>
         <label>Price: </label>
         <input onChange={handleInputChange} type="number" name="price"></input>
         <label>Description: </label>
         <textarea onChange={handleInputChange} name="description"></textarea>
         <label>Stock: </label>
         <input onChange={handleInputChange} type="number" name="stock"></input>
         <button type="submit">Create Product</button>


      </form>

   );
};

export default CreateProduct;
