import React from "react";
import {useDispatch} from "react-redux"
import {getVideogamesByName} from "../../redux/actions/index"

export default function SearchBar (){
     
        const dispatch = useDispatch()
        const [input, setInput] = React.useState({
            search: ""
        })
        const [games, setGames] = React.useState(false)
        const handleInputChange = function (e) {
            setInput({
                ...input,
                [e.target.name]: e.target.value
            });
        }
        function handleSubmit(e) {
            e.preventDefault()
            setGames(true)
            console.log(input, "soy input")
            dispatch(getVideogamesByName(input.search))
        }

    return(
        <div>
        <form onSubmit={e => handleSubmit(e)}>
            <input onChange={handleInputChange} type="text" name="search"></input>
            <button type="submit" >Buscar</button>
        </form>
        { games ? <Cards ></Cards> : null }
        </div>
    )
}
