import React from 'react';
import { createVideogame } from '../../redux/actions';
import { useDispatch } from 'react-redux';



const Create = () => {
    const dispatch = useDispatch()
    const [input, setInput] = React.useState({
        name: '',
        price: 0,
        description: '',
        stock: 0,
    })
    const handleInputChange = function (e) {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        });
    }
    function handleSubmit(e) {
        e.preventDefault()
        dispatch(createVideogame(input))
    }

    return (

        <form onSubmit={e => handleSubmit(e)}>


            <label>Name: </label>
            <input onChange={handleInputChange} name="name"></input>
            <label>Price: </label>
            <input onChange={handleInputChange} type="number" name="price"></input>
            <label>Description: </label>
            <textarea onChange={handleInputChange} name="description"></textarea>
            <label>Stock: </label>
            <input onChange={handleInputChange} type="number" name="stock"></input>
            <button type="submit">Create Product</button>


        </form>

    );
};

export default Create;
