import React, { useEffect } from "react";
import { getVideogames } from "../../redux/actions";
import { useSelector, useDispatch } from "react-redux";
import Card from "../Card/Card";
// import { useState } from "react";

export default function Cards(props){

    const dispatch = useDispatch();

    let videogames = useSelector(state => state.videogames)
  
    const [current, setCurrent] = useState(1);

    const [cardsPerPage, setCardsPerPage] = useState(15);
    const totalCards = cards.length
  
    useEffect(() => {
      dispatch(getVideogames())
    }, []);

    const pages = []

  for (let i = 1; i <= Math.ceil(totalCards / cardsPerPage); i++) {
    pages.push(i)
  }

  function paginate (pageNumber) {
    return setCurrent(pageNumber)
  }

  const filter = useSelector(state => state.filter)
    return(
        <div>
        <div>
            {videogames.map(v => <Card 
                id = {v.id}
                name = {v.name}
                image = {v.image}
                genres = {v.genres}
            />)}
        </div>
        <div className='cards'>
        {videogames && (filter.length ? filter : videogames).map((videogame, i) => {
          if(i >= current*15 - 15 && i < current * 15){
            console.log(i)
            return <Card key={videogame.id} videogame={videogame} />
          } else {
            return false
          }
        })}
      </div>
        </div>
    )
}
