import React, { useState } from "react";
import SearchBar from "../Searchbar/Searchbar";
import styles from "./Navbar.module.css"
import { useHistory } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { filter, filterByGenre, filterVideogames, getGenres } from "../../redux/actions";
import { useEffect } from "react";


export default function Navbar() {
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getGenres())
    }, []);
    let genres = useSelector(state => state.genres)

    let [genre, setGenre] = useState("");

    function handleGenreChange(e) {
        e.preventDefault()
        setGenre(e.target.value)
        dispatch(filter(genre))
    }

    let history = useHistory()
    return (
        <div className={styles.Navbar}>
            <SearchBar></SearchBar>
            <button onClick={() => { history.push("/create") }}>Create</button>
            <button onClick={() => { history.push("/home") }}>Home</button>
            <select >
                <option>Sort Alphabetically</option>
                <option value="az">A-Z</option>
                <option value="za">Z-A</option>
            </select>
            <select >
                <option >Sort Rating</option>
                <option value="all">All</option>
                <option value="best">Best rating</option>
                <option value="worst">Worst rating</option>
            </select>
            <select >
                <option >Filter by Source</option>
                <option value="all">All</option>
                <option value="api">API</option>
                <option value="created">Created</option>
            </select>
            <select onChange={(e) => handleGenreChange(e)}>
                <option >Filter by Genres</option>
                <option value="all">All</option>
                {genres.map(g => <option name={g.name}>{g.name}</option>)}
            </select>
            <button onClick={"xd"}>Reset Filters</button>
        </div>
    )
}
