import React, { useEffect, useState } from "react";
import { getVideogames } from "../../redux/actions";
import { useSelector, useDispatch } from "react-redux";
import styles from "./Cards.module.css";
import Card from "../Card/Card";

export default function Cards(props) {

    const dispatch = useDispatch();

    let videogames = useSelector(state => state.videogames)
    let filterVideogames = useSelector(state => state.filter)

    const [current, setCurrent] = useState(1);

    const cardsPerPage = 15;
    const totalCards = videogames.length

    useEffect(() => {
        filterVideogames.length ?
        dispatch(filterVideogames())
        : dispatch(getVideogames())
    }, []);

    const pages = []

    for (let i = 1; i <= Math.ceil(totalCards / cardsPerPage); i++) {
        pages.push(i)
    }

    function paginate(pageNumber) {
        if(pageNumber > pages.length || pageNumber < 1) {return}
        return setCurrent(pageNumber)
    }
    return (
        <div className={styles.body}>
            <div className={styles.cards}>
                {videogames.map((v, i) => {
                    if (i >= current * 15 - 15 && i < current * 15) {
                        console.log(i)
                        return <Card id={v.id}
                            name={v.name}
                            image={v.image}
                            genres={v.genres} />
                    } else {
                        return false
                    }
                })}
            </div>
            <div className={styles.pagination}>
            <button onClick={() =>  paginate(current - 1)}>BACK</button>
                {pages.map(page => (
                    <div className={styles.paginateButtons} key={page}>
                        <a className={styles.buttons} onClick={() => paginate(page)}>
                            {page}
                        </a>
                    </div>
                ))}
            <button onClick={() =>  paginate(current + 1)}>NEXT</button>
            </div>
        </div>
    )
}
